import time
import socket

# cf. les notes dans le code du serveur

HOST = ''
PORT = 55555

data_1kb = b""

with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s:
    s.connect((HOST, PORT))
    counter = 0
    while True:
        s.sendall(data_1kb)
        print(counter)
        counter += 1
        time.sleep(0.01)
